INSERT [dbo].[Vaccines] ([Id], [Contraindication], [Indication], [Number_Of_Injection], [Origin], [Time_Begin_Next_Injection], [Time_End_Next_Injection], [Usage], [Vaccine_Name], [Purchase_Price], [Selling_Price], [Image], [Description], [Required_Injections], [Time_Between_Injections], [Status], [Vaccine_Type_Id]) VALUES (N'VC000058', N'Dị ứng với latex', N'Trẻ từ 6 tuần đến 2 tuổi', 3, N'Hà Lan', CAST(N'2024-11-01' AS Date), CAST(N'2026-11-10' AS Date), N'Tiêm bắp', N'Comvax', 1200000, 1500000, N'/Uploads/Vaccine/602afaf4-b86a-440c-8246-7b92269803c8.png', N'Comvax cung cấp miễn dịch rộng rãi cho trẻ nhỏ.', 3, 45, 1, N'VT000010')
INSERT [dbo].[Vaccines] ([Id], [Contraindication], [Indication], [Number_Of_Injection], [Origin], [Time_Begin_Next_Injection], [Time_End_Next_Injection], [Usage], [Vaccine_Name], [Purchase_Price], [Selling_Price], [Image], [Description], [Required_Injections], [Time_Between_Injections], [Status], [Vaccine_Type_Id]) VALUES (N'VC000059', N'Không', N'Trẻ sơ sinh dưới 1 tuổi', 4, N'Ấn Độ', CAST(N'2024-12-01' AS Date), CAST(N'2026-12-10' AS Date), N'Tiêm bắp', N'Polivax', 900000, 1200000, N'/Uploads/Vaccine/cdc5cc0f-3951-4245-8ecd-8ca7f615d064.png', N'Polivax là vắc-xin phổ biến chống lại bại liệt.', 4, 30, 1, N'VT000010')
INSERT [dbo].[Vaccines] ([Id], [Contraindication], [Indication], [Number_Of_Injection], [Origin], [Time_Begin_Next_Injection], [Time_End_Next_Injection], [Usage], [Vaccine_Name], [Purchase_Price], [Selling_Price], [Image], [Description], [Required_Injections], [Time_Between_Injections], [Status], [Vaccine_Type_Id]) VALUES (N'VC000060', N'Dị ứng với protein sữa', N'Trẻ em từ 1-6 tuổi', 3, N'Hàn Quốc', CAST(N'2025-01-01' AS Date), CAST(N'2025-12-31' AS Date), N'Tiêm bắp', N'Tetraxim', 950000, 1250000, N'/Uploads/Vaccine/e469f3a4-8543-42cb-8113-136ffd4f16ea.png', N'Tetraxim bảo vệ khỏi 4 bệnh nguy hiểm: bạch hầu, uốn ván, ho gà, và bại liệt.', 3, 45, 1, N'VT000010')
INSERT [dbo].[Vaccines] ([Id], [Contraindication], [Indication], [Number_Of_Injection], [Origin], [Time_Begin_Next_Injection], [Time_End_Next_Injection], [Usage], [Vaccine_Name], [Purchase_Price], [Selling_Price], [Image], [Description], [Required_Injections], [Time_Between_Injections], [Status], [Vaccine_Type_Id]) VALUES (N'VC000061', N'Dị ứng với gelatin', N'Trẻ em từ 6 tháng đến 12 tuổi', 1, N'Pháp', CAST(N'2024-11-01' AS Date), CAST(N'2026-04-10' AS Date), N'Tiêm bắp', N'FluMaster Junior', 125000, 195000, N'/Uploads/Vaccine/d5ebfeff-5a36-4d43-bfa8-bac72cf9c833.png', N'FluMaster Junior là vắc-xin phòng ngừa cúm mùa với hiệu quả bảo vệ cao. Được sản xuất bằng công nghệ tiên tiến, vắc-xin này giúp bảo vệ trẻ em khỏi hai chủng cúm A và hai chủng cúm B phổ biến nhất hiện nay. Đặc biệt, FluMaster Junior được thiết kế với hàm lượng kháng nguyên thấp, đảm bảo an toàn cho trẻ nhỏ. Thận trọng khi sử dụng: 
Đối tượng đang sốt cao cấp tính (hoãn tiêm cho đến khi trẻ hết sốt).
Trẻ bị rối loạn thần kinh tiến triển bao gồm chứng co thắt, động kinh không kiểm soát hoặc bệnh não tiến triển (hoãn tiêm cho đến khi bệnh khỏi hoặc ổn định, hoặc cân nhắc nguy cơ/lợi ích).
Cân nhắc sử dụng đối với trường hợp sau tiêm chủng vắc xin có thành phần ho gà trong vòng 48 giờ có biểu hiện sốt, quấy khóc…
Tác dụng không mong muốn:
Trẻ em 4-9 tuổi: Có thể chán ăn, ngủ gà gật, nhạy cảm chỗ tiêm, sốt, nôn, tiêu chảy.
Người lớn, thanh thiếu niên và trẻ em ≥10 tuổi: Có thể đau đầu, nhạy cảm chỗ tiêm, mệt mỏi, khó chịu chóng mặt; buồn nôn, rối loạn tiêu hóa.
Tương tác thuốc:
Nếu đang dùng thuốc hoặc muốn tiêm cùng với các vắc xin khác vào cùng một thời điểm, hãy hỏi ý kiến của bác sĩ hoặc dược sĩ.
Bảo quản:
Vắc xin được bảo quản ở nhiệt độ từ 2 đến 8 độ C. Không được để đông băng.', 1, 30, 1, N'VT000001')
INSERT [dbo].[Vaccines] ([Id], [Contraindication], [Indication], [Number_Of_Injection], [Origin], [Time_Begin_Next_Injection], [Time_End_Next_Injection], [Usage], [Vaccine_Name], [Purchase_Price], [Selling_Price], [Image], [Description], [Required_Injections], [Time_Between_Injections], [Status], [Vaccine_Type_Id]) VALUES (N'VC000062', N'Dị ứng với protein trứng', N'Người lớn trên 18 tuổi', 1, N'Mỹ', CAST(N'2025-01-01' AS Date), CAST(N'2026-06-30' AS Date), N'Tiêm bắp', N'FluShield Adult', 135000, 205000, N'/Uploads/Vaccine/53941433-2e93-48ed-84a5-67092899b0ba.png', N'FluShield Adult là dòng vắc-xin được phát triển đặc biệt cho người trưởng thành, giúp tăng cường hệ miễn dịch để chống lại virus cúm. Vắc-xin này đã được kiểm nghiệm lâm sàng trên hơn 10,000 người, cho thấy hiệu quả 95% trong việc ngăn ngừa bệnh cúm mùa. FluShield Adult cũng được bổ sung các tá dược tiên tiến, giảm thiểu phản ứng phụ. Thận trọng khi sử dụng: 
Đối tượng đang sốt cao cấp tính (hoãn tiêm cho đến khi trẻ hết sốt).
Trẻ bị rối loạn thần kinh tiến triển bao gồm chứng co thắt, động kinh không kiểm soát hoặc bệnh não tiến triển (hoãn tiêm cho đến khi bệnh khỏi hoặc ổn định, hoặc cân nhắc nguy cơ/lợi ích).
Cân nhắc sử dụng đối với trường hợp sau tiêm chủng vắc xin có thành phần ho gà trong vòng 48 giờ có biểu hiện sốt, quấy khóc…
Tác dụng không mong muốn:
Trẻ em 4-9 tuổi: Có thể chán ăn, ngủ gà gật, nhạy cảm chỗ tiêm, sốt, nôn, tiêu chảy.
Người lớn, thanh thiếu niên và trẻ em ≥10 tuổi: Có thể đau đầu, nhạy cảm chỗ tiêm, mệt mỏi, khó chịu chóng mặt; buồn nôn, rối loạn tiêu hóa.
Tương tác thuốc:
Nếu đang dùng thuốc hoặc muốn tiêm cùng với các vắc xin khác vào cùng một thời điểm, hãy hỏi ý kiến của bác sĩ hoặc dược sĩ.
Bảo quản:
Vắc xin được bảo quản ở nhiệt độ từ 2 đến 8 độ C. Không được để đông băng.', 1, 45, 1, N'VT000001')
